mapper.select:
select uheight,umerried,uhouse,agerange,uincome,ucar,uedu,uwork,ublood,
constellation,uaddress,s_province,s_city from tblcplimit
where userid = #{userid}

mapper.update:
UPDATE tblcplimit SET uheight = #{uheight},umerried = #{umerried},uhouse = #{uhouse},
agerange = #{agerange},uincome = #{uincome},ucar = #{ucar},uedu = #{uedu},uwork = #{uwork},
ublood = #{ublood},constellation = #{constellation},uaddress = #{uaddress},s_province = #{s_province},s_city = #{s_city}
WHERE userid = #{userid}

