mapper.select:
select i.* , p.pname from tblinform i , tblparam p where i.itype = p.paramid

    and itype = #{itype}


    and ititle like '%${ititle}%'


    and itime >= #{begindate}


    and itime <= #{enddate}

ORDER BY itime desc
limit #{page},#{limit}

select count(*) from tblinform


        and itype = #{itype}


        and ititle like '%${ititle}%'


        and itime >= #{begindate}


        and itime <= #{enddate}
